INSERT INTO public.faqs (question, answer, display_order) VALUES
('ما هي الخدمات التي تقدمها شركة بيكسل ؟', 'نقدم خدمات شاملة في التسويق الرقمي، تصميم المواقع، إدارة وسائل التواصل الاجتماعي، الإعلانات الممولة، وتحسين محركات البحث SEO.', 1),
('ما الذي يميز بيكسل عن الوكالات الإعلانية الأخرى؟', 'نتميز بفريق متخصص ذو خبرة واسعة، استراتيجيات مبتكرة، وحلول مخصصة تناسب احتياجات كل عميل، مع التركيز على تحقيق نتائج ملموسة.', 2),
('ما هي الفائدة من التعامل مع شركة بيكسل للتسويق الإلكتروني؟', 'نساعدك في بناء حضور قوي على الإنترنت، زيادة الوعي بالعلامة التجارية، جذب عملاء جدد، وتحقيق عائد استثماري أفضل من خلال استراتيجيات تسويقية متقدمة.', 3),
('ما هو الوقت الذي تستغرقه المطلوبة لرؤية الخدمة؟', 'يختلف الوقت حسب نوع الخدمة والأهداف المطلوبة. عادةً ما نبدأ برؤية نتائج ملموسة خلال 3-6 أشهر، مع تحسينات مستمرة مع الوقت.', 4),
('كيف يمكنني التواصل مع شركة بيكسل؟', 'يمكنك التواصل معنا عبر الهاتف، البريد الإلكتروني، أو من خلال نموذج الاتصال على موقعنا. فريقنا جاهز للرد على استفساراتك في أي وقت.', 5);
